import React, { useState } from "react";
import { Badge, Button, Dialog, DialogContent, DialogTitle } from "@mui/material";
import { useLocation, useNavigate } from "react-router-dom";
import CartItem from "../Cart/CartItem";
import { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getOrderById } from "../../../Redux/Customers/Order/Action";
import AddressCard from "../adreess/AdreessCard";
import { createPayment } from "../../../Redux/Customers/Payment/Action";
import DummyRazorpayForm from "./DummyRazorPay";

const OrderSummary = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const searchParams = new URLSearchParams(location.search);
  const orderId = searchParams.get("order_id");
  const dispatch = useDispatch();
  const jwt = localStorage.getItem("jwt");
  const { order } = useSelector((state) => state);

  console.log("orderId ", order);

  useEffect(() => {
    dispatch(getOrderById(orderId));
  }, [orderId]);

  // const handleCreatePayment = () => {
  //   // const data = { orderId: order.order?._id, jwt };
  //   // dispatch(createPayment(data));
  //   <DummyRazorpayForm/>
  // };
  const [open, setOpen] = useState(false);

  // Function to handle the opening of the dialog
  const handleCreatePayment = () => {
    setOpen(true);
  };

  // Function to handle the closing of the dialog
  const handleClose = () => {
    setOpen(false);
  };

  return (
    <div className="space-y-5">
      <div className="p-5 shadow-lg rounded-md border ">
        <AddressCard address={order.order?.shippingAddress} />
      </div>
      <div className="lg:grid grid-cols-3 relative justify-between">
        <div className="lg:col-span-2 ">
          <div className=" space-y-3">
            {order.order?.orderItems.map((item) => (
              <>
                <CartItem item={item} showButton={true} />
              </>
            ))}
          </div>
        </div>
        <div className="sticky top-0 h-[100vh] mt-5 lg:mt-0 ml-5" style={{color:"#463f6a"}}>
          <div className="border p-5 bg-white shadow-black shadow-lg rounded-md" style={{background:"#e8ba88"}}>
            <p className="font-bold opacity-60 pb-4">PRICE DETAILS</p>
            <hr />

            <div className="space-y-3 font-semibold">
              <div className="flex justify-between pt-3 text-black ">
                <span>Price ({order.order?.totalItem} item)</span>
                <span>₹{order.order?.totalPrice}</span>
              </div>
              <div className="flex justify-between">
                <span>Discount</span>
                <span className="text-green-700">
                  -₹{order.order?.discounte}
                </span>
              </div>
              <div className="flex justify-between">
                <span>Delivery Charges</span>
                <span className="text-green-700">Free</span>
              </div>
              <hr />
              <div className="flex justify-between font-bold text-lg">
                <span>Total Amount</span>
                <span className="text-green-700">
                  ₹{order.order?.totalDiscountedPrice}
                </span>
              </div>
            </div>

            <Button
              onClick={handleCreatePayment}
              variant="contained"
              type="submit"
              sx={{ padding: ".8rem 2rem", marginTop: "2rem", width: "100%" }}
            >
              Payment
            </Button>
            {/* Dialog component that acts as a popover panel */}
            <Dialog open={open} onClose={handleClose}>
              <DialogTitle>Payment</DialogTitle>
              <DialogContent>
                <DummyRazorpayForm
                  handleNext={() => {
                    console.log("Payment successful");
                    handleClose(); // Close the dialog after submitting the form
                  }}
                />
              </DialogContent>
            </Dialog>
          </div>
        </div>
      </div>
    </div>
  );
};

export default OrderSummary;
