import React from "react";
import "./ProductCard.css";
import { useLocation, useNavigate } from "react-router-dom";

const ProductCard = ({ product }) => {
  const {
    title,
    brand,
    imageUrl,
    price,
    discountedPrice,
    color,
    discountPersent,
  } = product;
  const navigate = useNavigate();

  const handleNavigate = () => {
    navigate(`/product/${product?._id}`);
  };

  return (
    <div
      onClick={handleNavigate}
      className="productCard w-[15rem] h-[30rem] border m-3 transition-all cursor-pointer flex flex-col"
    >
      <div className="h-[20rem]">
        <img
          className="h-full w-full object-cover object-left-top"
          src={imageUrl}
          alt=""
        />
      </div>
      <div className="textPart bg-white p-3 flex-1 flex flex-col justify-between">
        <div>
          <p className="font-bold opacity-60">{brand}</p>
          <p
            className="line-clamp-2"
            style={{
              overflowWrap: "break-word",
              maxHeight: "3em", // Adjust based on your font-size and desired number of lines
              lineHeight: "1.5em", // This should match the 'max-height' divided by the number of lines
              display: "-webkit-box",
              WebkitLineClamp: "2",
              WebkitBoxOrient: "vertical",
              overflow: "hidden",
              textOverflow: "ellipsis",
            }}
          >
            {title}
          </p>
          <p className="font-semibold opacity-60">{color}</p>
        </div>
        <div className="flex space-x-2 items-center">
          <p className="font-semibold">₹{discountedPrice}</p>
          <p className="opacity-50 line-through">₹{price}</p>
          <p className="text-green-600 font-semibold">{discountPersent}% off</p>
        </div>
      </div>
    </div>
  );
};

export default ProductCard;
