import React from 'react';
import { useNavigate } from 'react-router-dom';

const HomeProductCard = ({ product }) => {
  const navigate = useNavigate();

  return (
    <div
    key={product._id}
    onClick={() => navigate(`/product/${product._id}`)}
    className="bg-lime-100 text-white cursor-pointer flex flex-col items-center rounded-lg shadow-lg overflow-hidden w-[15rem] h-[25rem] mx-3" // Adjusted height here
  >
    <div className="h-[13rem] w-[10rem] flex items-center justify-center"> {/* Added flex and justify-center classes */}
      <img
        className="object-cover w-full h-full"
        alt={product.title}
        src={product.imageUrl}
        style={{ maxHeight: '100%', maxWidth: '100%' }} // Added styles to maintain aspect ratio
      />
    </div>
    <div className="p-4">
      <h3 className="text-lg font-medium text-gray-900">
        {product.title}
      </h3>
      <p className="mt-2 text-sm text-gray-500">
        {product.description} {/* Assuming product.description is the actual description */}
      </p>
    </div>
  </div>
  
  );
};

export default HomeProductCard;
